"""Collections of constants used in this package."""

# Name of this package, to allow for an easier renaming of this package in the
# future.
PACKAGE_NAME = "houdini_deadline_api_submission"

# Deadline plugin names.
ARNOLD_PLUGIN = "hal_arnold"
HOUDINI_PLUGIN = "hal_houdini"
MAYA_PLUGIN = "hal_mayabatch"
MANTRA_PLUGIN = "hal_mantra"
NUKE_PLUGIN = "hal_nuke"
CMD_LINE_PLUGIN = "hal_commandline"

# Logging parameters.
LOG_FOLDER = "HAL Log"  # Name of the parameter folder parent to the log parm.
LOG_PARM = "hal_submission_log"  # Name of the parameter that stores logs.

# Wedging parameters.
# Name of the folder where wedging parameters will be added to.
WEDGE_MAIN_FOLDER = "HAL Wedge"
# Checkbox parameter that indicates if wedges should be used.
WEDGE_USE_WEDGE = "hal_use_hal_wedges"
# MultiParameter that stores the wedges.
WEDGE_PARMS = "hal_wedge_parms"
# Parameter for the name of a single wedge.
# {id} needs to be given since this will be replaced with the MultiParm id.
WEDGE_NAME = "hal_wedge_name{id}"
# Parameter for the channel reference to the parameter that will be wedged.
# {id} needs to be given since this will be replaced with the MultiParm id.
WEDGE_CHANNEL = "hal_wedge_chan{id}"
# Parameter for the range of a single wedge.
# {id} needs to be given since this will be replaced with the MultiParm id.
WEDGE_RANGE = "hal_wedge_range{id}"
# Parameter for the amount of steps between the given range.
# {id} needs to be given since this will be replaced with the MultiParm id.
WEDGE_STEPS = "hal_wedge_steps{id}"

# Override parameters.
# Prefix for Job Info override parameters.
JOB_OVERRIDE_PARM_PREFIX = "hal_job_override_"
# Prefix for Plugin Info override parameters.
PLUGIN_OVERRIDE_PARM_PREFIX = "hal_plugin_override_"
# Prefix for the checkbox created for each override.
OVERRIDE_CHECK_PREFIX = "do_override_"

# Extra Plugin options.
# Parameter that marks a ROP as PrePass job (executed before submission).
IS_PRE_PASS = "hal_pre_pass"
# Parameter that ensures every ROP inside this parent ROP is submitted as it's
# own job.
SUBMIT_CHILDREN_SEPARATELY = "hal_submit_children_separately"
# Name of the Render Mode drop-down parameter, which indicates how to render a
# Render ROP (Mantra, Arnold, ...).
RENDER_MODE = "hal_render_mode"
# Index of the Cache & Render configuration inside the Render Mode drop-down.
RENDER_MODE_CACHE_RENDER = 0
# Index of the Cache Only value inside the Render Mode drop-down.
RENDER_MODE_CACHE_ONLY = 1
# Index of the Render Only value inside the Render Mode drop-down.
RENDER_MODE_RENDER_ONLY = 2
